query AvSearch($limit: Int!, $offset: Int, $floor: PPVFloor, $sort: ContentSearchPPVSort!, $queryWord: String, $filter: ContentSearchPPVFilterInput, $facetLimit: Int!, $hasFacet: Boolean!, $hasGenreDescription: Boolean!, $legacyProductType: LegacyProductType = DOWNLOAD, $hasLegacyProductType: Boolean!, $isLoggedIn: Boolean!, $excludeUndelivered: Boolean!, $shouldFetchGenreRelatedWords: Boolean!, $shouldFetchContentTagIds: Boolean!, $shouldFetchDirectorRelatedWords: Boolean!, $shouldFetchLabelRelatedWords: Boolean!, $shouldFetchSeriesRelatedWords: Boolean!, $shouldFetchActressRelatedWords: Boolean!, $shouldFetchMakerRelatedWords: Boolean!, $shouldFetchHistrionRelatedWords: Boolean!) {
  legacySearchPPV(
    limit: $limit
    offset: $offset
    floor: $floor
    sort: $sort
    queryWord: $queryWord
    filter: $filter
    facetLimit: $facetLimit
    includeExplicit: true
    excludeUndelivered: $excludeUndelivered
  ) {
    result {
      contents {
        ...searchContent
        contentType
        actresses {
          id
          name
          __typename
        }
        maker {
          id
          name
          __typename
        }
        __typename
      }
      facet @include(if: $hasFacet) {
        ...contentSearchFacet
        __typename
      }
      pageInfo {
        ...paginationFragment
        __typename
      }
      isNoIndex
      searchCriteria {
        ...contentSearchCriteria
        __typename
      }
      osusumeGalleryLinks {
        text
        url
        __typename
      }
      __typename
    }
    __typename
  }
}
fragment searchContent on PPVContentSearchContent {
  id
  title
  packageImage {
    mediumUrl
    largeUrl
    __typename
  }
  sampleImages {
    number
    largeUrl
    __typename
  }
  sampleMovie {
    hlsUrl
    mp4Url
    vrUrl
    __typename
  }
  releaseStatus
  review {
    average
    count
    __typename
  }
  isExclusiveDelivery
  bookmarkCount
  salesInfo {
    lowestPrice {
      productId
      price
      discountPrice
      legacyProductType
      __typename
    }
    priceByLegacyProductType(legacyProductType: $legacyProductType) @include(if: $hasLegacyProductType) {
      discountPrice
      price
      legacyProductType
      __typename
    }
    campaign {
      name
      endAt
      __typename
    }
    pointRewardCampaign {
      name
      __typename
    }
    hasMultiplePrices
    __typename
  }
  isOnSale
  deliveryStartAt
  utilizationStatus @include(if: $isLoggedIn)
  __typename
}
fragment contentSearchFacet on PPVContentSearchFacet {
  floor {
    items {
      floor
      count
      __typename
    }
    __typename
  }
  actress {
    items {
      id
      name
      count
      __typename
    }
    __typename
  }
  maker {
    items {
      id
      name
      count
      __typename
    }
    __typename
  }
  label {
    items {
      id
      name
      count
      __typename
    }
    __typename
  }
  series {
    items {
      id
      name
      count
      __typename
    }
    __typename
  }
  genreAndCampaignCombined {
    items {
      ... on GenreFacetItem {
        count
        id
        name
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}
fragment paginationFragment on OffsetPageInfoWithTotal {
  offset
  limit
  hasNext
  totalCount
  __typename
}
fragment contentSearchCriteria on PPVContentSearchCriteria {
  sort
  filter {
    actressIds {
      ids {
        id
        name
        nameRuby
        relatedWords @include(if: $shouldFetchActressRelatedWords)
        __typename
      }
      op
      __typename
    }
    authorIds {
      ids {
        id
        name
        nameRuby
        __typename
      }
      op
      __typename
    }
    directorIds {
      ids {
        id
        name
        nameRuby
        relatedWords @include(if: $shouldFetchDirectorRelatedWords)
        __typename
      }
      op
      __typename
    }
    genreIds {
      ids {
        id
        name
        relatedWords @include(if: $shouldFetchGenreRelatedWords)
        description @include(if: $hasGenreDescription)
        __typename
      }
      op
      __typename
    }
    histrionIds {
      ids {
        id
        name
        nameRuby
        relatedWords @include(if: $shouldFetchHistrionRelatedWords)
        __typename
      }
      op
      __typename
    }
    labelIds {
      ids {
        id
        name
        relatedWords @include(if: $shouldFetchLabelRelatedWords)
        __typename
      }
      op
      __typename
    }
    makerIds {
      ids {
        id
        name
        relatedWords @include(if: $shouldFetchMakerRelatedWords)
        __typename
      }
      op
      __typename
    }
    seriesIds {
      ids {
        id
        name
        relatedWords @include(if: $shouldFetchSeriesRelatedWords)
        __typename
      }
      op
      __typename
    }
    saleIds {
      ids {
        id
        name
        __typename
      }
      op
      __typename
    }
    pointRewardCampaignIds {
      ids {
        id
        name
        __typename
      }
      op
      __typename
    }
    contentTagIds @include(if: $shouldFetchContentTagIds) {
      ids {
        id
        name
        __typename
      }
      op
      __typename
    }
    isSaleItemsOnly
    __typename
  }
  __typename
}
