query:
query ContentPageData($id: ID!, $isLoggedIn: Boolean!, $isAmateur: Boolean!, $isAnime: Boolean!, $isAv: Boolean!, $isCinema: Boolean!, $isSP: Boolean!, $shouldFetchRelatedTags: Boolean = false) {
  ppvContent(id: $id) {
    ...ContentData
    __typename
  }
  reviewSummary(contentId: $id) {
    ...ReviewSummary
    __typename
  }
  ...basketCountFragment @include(if: $isSP)
}
fragment ContentData on PPVContent {
  id
  floor
  title
  isExclusiveDelivery
  releaseStatus
  description
  notices
  isNoIndex
  isAllowForeign
  announcements {
    body
    __typename
  }
  featureArticles {
    link {
      url
      text
      __typename
    }
    __typename
  }
  packageImage {
    largeUrl
    mediumUrl
    __typename
  }
  sampleImages {
    number
    imageUrl
    largeImageUrl
    __typename
  }
  products {
    ...ProductData
    __typename
  }
  mostPopularContentImage {
    ... on ContentSampleImage {
      __typename
      largeImageUrl
      imageUrl
    }
    ... on PackageImage {
      __typename
      largeUrl
      mediumUrl
    }
    __typename
  }
  pricing {
    lowestEffectivePriceInclusiveTax
    lowestRegularPriceInclusiveTax
    sale {
      name
      id
      endAt
      __typename
    }
    pointRewardCampaign {
      name
      id
      endAt
      promotionId
      rate
      __typename
    }
    __typename
  }
  weeklyRanking: ranking(term: Weekly)
  monthlyRanking: ranking(term: Monthly)
  wishlistCount
  sample2DMovie {
    highestMovieUrl
    hlsMovieUrl
    __typename
  }
  sampleVRMovie {
    highestMovieUrl
    __typename
  }
  ...AmateurAdditionalContentData @include(if: $isAmateur)
  ...AnimeAdditionalContentData @include(if: $isAnime)
  ...AvAdditionalContentData @include(if: $isAv)
  ...CinemaAdditionalContentData @include(if: $isCinema)
  __typename
}
fragment ProductData on PPVProduct {
  id
  priority
  deliveryUnit {
    id
    priority
    streamMaxQualityGroup
    downloadMaxQualityGroup
    __typename
  }
  pricing {
    regularPriceInclusiveTax
    effectivePriceInclusiveTax
    __typename
  }
  expireDays
  utilizationStatus @include(if: $isLoggedIn)
  licenseType
  shopName
  couponDiscount {
    coupon {
      name
      expirationPolicy {
        ... on CouponExpirationAt {
          expirationAt
          __typename
        }
        ... on CouponExpirationDay {
          expirationDays
          __typename
        }
        __typename
      }
      expirationAt
      minPayment
      destinationUrl
      __typename
    }
    discountedPriceInclusiveTax
    __typename
  }
  __typename
}
fragment AmateurAdditionalContentData on PPVContent {
  deliveryStartDate
  duration
  amateurActress {
    id
    name
    imageUrl
    age
    waist
    bust
    bustCup
    height
    hip
    relatedContents {
      id
      title
      __typename
    }
    __typename
  }
  maker {
    id
    name
    __typename
  }
  label {
    id
    name
    __typename
  }
  genres {
    id
    name
    __typename
  }
  makerContentId
  playableInfo {
    ...PlayableInfo
    __typename
  }
  __typename
}
fragment PlayableInfo on PlayableInfo {
  playableDevices {
    deviceDeliveryUnits {
      id
      deviceDeliveryQualities {
        isDownloadable
        isStreamable
        __typename
      }
      __typename
    }
    device
    name
    priority
    isSupported
    __typename
  }
  deviceGroups {
    id
    devices {
      deviceDeliveryUnits {
        id
        deviceDeliveryQualities {
          isStreamable
          isDownloadable
          __typename
        }
        __typename
      }
      isSupported
      __typename
    }
    __typename
  }
  vrViewingType
  __typename
}
fragment AnimeAdditionalContentData on PPVContent {
  deliveryStartDate
  duration
  series {
    id
    name
    __typename
  }
  maker {
    id
    name
    __typename
  }
  label {
    id
    name
    __typename
  }
  genres {
    id
    name
    __typename
  }
  makerContentId
  playableInfo {
    ...PlayableInfo
    __typename
  }
  __typename
}
fragment AvAdditionalContentData on PPVContent {
  deliveryStartDate
  makerReleasedAt
  duration
  actresses {
    id
    name
    nameRuby
    imageUrl
    isBookmarked @include(if: $isLoggedIn)
    __typename
  }
  histrions {
    id
    name
    __typename
  }
  directors {
    id
    name
    __typename
  }
  series {
    id
    name
    __typename
  }
  maker {
    id
    name
    __typename
  }
  label {
    id
    name
    __typename
  }
  genres {
    id
    name
    __typename
  }
  contentType
  relatedWords @skip(if: $shouldFetchRelatedTags)
  relatedTags(limit: 16) @include(if: $shouldFetchRelatedTags) {
    ... on ContentTagGroup {
      tags {
        id
        name
        __typename
      }
      __typename
    }
    ... on ContentTag {
      id
      name
      __typename
    }
    __typename
  }
  makerContentId
  playableInfo {
    ...PlayableInfo
    __typename
  }
  __typename
}
fragment CinemaAdditionalContentData on PPVContent {
  deliveryStartDate
  duration
  actresses {
    id
    name
    nameRuby
    imageUrl
    __typename
  }
  histrions {
    id
    name
    __typename
  }
  directors {
    id
    name
    __typename
  }
  authors {
    id
    name
    __typename
  }
  series {
    id
    name
    __typename
  }
  maker {
    id
    name
    __typename
  }
  label {
    id
    name
    __typename
  }
  genres {
    id
    name
    __typename
  }
  makerContentId
  playableInfo {
    ...PlayableInfo
    __typename
  }
  __typename
}
fragment ReviewSummary on ReviewSummary {
  average
  total
  withCommentTotal
  distributions {
    total
    withCommentTotal
    rating
    __typename
  }
  __typename
}
fragment basketCountFragment on Query {
  legacyBasket @skip(if: $isLoggedIn) {
    total
    __typename
  }
  basketCount: user @include(if: $isLoggedIn) {
    ... on Member {
      ppvBasketItemCount
      __typename
    }
    __typename
  }
  __typename
}
